require 'spec_helper'

describe 'wordpeppers::deploy' do
  describe user('ubuntu') do
      it { should exist }
      it { should have_authorized_key 'ssh-rsa  vagrant insecure public key' }
  end

  describe file('/home/ubuntu/.ssh/id_rsa') do
    it { should be_mode 600 }
    it { should be_owned_by 'ubuntu' }
  end

  describe x509_private_key('/home/ubuntu/.ssh/id_rsa') do
    it { should be_valid }
  end
end
